import React, { useState, useEffect } from "react";
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import Pagination from 'react-bootstrap/Pagination';
import axios from "axios"
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';


// let active = 1;
// let items = [];
// for (let number = 1; number <= 5; number++) {
//   items.push(
//     <Pagination.Item key={number} active={number === active}>
//       {number}
//     </Pagination.Item>,
//   );
// }


const TableData = (props) => {
    const [data, setData] = useState([]);

    useEffect(() => {
        axios
        .get(`http://localhost:3000/${props.url}`)
        .then((response) => setData(response.data));
  }, []);

    return (
        <>
            <Table striped bordered hover size="sm">
                <thead>
                    <tr >
                        <th>ID</th>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Amount $</th>
                        <th>Time</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data?.map((value, key) => {
                            return (
                                <tr key={key}>
                                    <td>{value.id}</td>
                                    <td>{value.name}</td>
                                    <td>{value.description}</td>
                                    <td>{value.amount}</td>
                                    <td>{value.time}</td>
                                    <td>
                                    <Button variant="success" onClick={handleShow}>ADD</Button>{' '}
                                    <Modal show={show} onHide={handleClose}>
      <Form noValidate validated={validated} onSubmit={onSubmit}>
        <Modal.Header closeButton>
          <Form.Select aria-label="Default select example" required onChange={onSelectChange} >
              <option value="">Select Group</option>
              <option value="expense">Expense Group</option>
              <option value="income">Income Group</option>
          </Form.Select>
        </Modal.Header>

        <Modal.Body>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">

            <Form.Label>Name</Form.Label>
              <Form.Control
                onChange={onNameChange}
                required
                type="text"
                placeholder="Please full the empty field"
                autoFocus /> 
                <Form.Control.Feedback type="invalid">Name not specified</Form.Control.Feedback>  

              <Form.Label>Amount</Form.Label>
              <Form.Control
                onChange={onAmountChange}
                required
                type="number"
                placeholder="Please enter amount $..."
                autoFocus
              /> 
              <Form.Control.Feedback type="invalid">
              Amount not specified
          </Form.Control.Feedback>
            </Form.Group>
            <Form.Group
              className="mb-3"
              controlId="exampleForm.ControlTextarea1"
            >

              <Form.Label>Desciption</Form.Label> 
              <Form.Control
              onChange={onDescriptionChange}
              required  
              type="text"
              placeholder="Please enter a description..."
              as="textarea" rows={2}/>
              <Form.Control.Feedback type="invalid">
              Description not specified
          </Form.Control.Feedback>
            </Form.Group>
        </Modal.Body>
        </Form>
      </Modal>
                                    <Button variant="info">EDIT</Button>{' '}
                                    <Button variant="danger">DELETE</Button>{' '}
                                    </td>

                                </tr>
                            )
                        })
                    }
                </tbody>
                {/* <div>
                <Pagination size="sm">{items}</Pagination>
                </div> */}
            </Table>
        </>
    );
}

export default TableData;
