import React, { useState, useEffect } from "react";
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import Pagination from 'react-bootstrap/Pagination';
import axios from "axios"
import { useNavigate } from "react-router-dom";
import Modal from 'react-bootstrap/Modal';




// let active = 1;
// let items = [];
// for (let number = 1; number <= 5; number++) {
//   items.push(
//     <Pagination.Item key={number} active={number === active}>
//       {number}
//     </Pagination.Item>,
//   );
// }


const TableData = (props) => {
    const [data, setData] = useState([]);
    const [show, setShow] = useState(false);
    const [deleteId, setDeleteId] = useState("");

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const navigate = useNavigate()
    useEffect(() => {
        axios
        .get(`http://localhost:3000/${props.url}`)
        .then((response) => setData(response.data));
  }, [])

  const handleOpenDeleteModal = (ID) => {
    setDeleteId(ID)
    setShow(true);
}

const handleDelete = () => {
    axios
    .delete(`http://localhost:3000/${props.url}/${deleteId}`)
    .then(response => {
        axios
        .get(`http://localhost:3000/${props.url}`)
        .then((response) => setData(response.data))
        .finally (handleClose());

})
}



    return (
        <>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Warning</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        Are you sure you want to delete this item?

        </Modal.Body>
        <Modal.Footer>
          <Button variant="success" onClick={handleClose}>
            Close
          </Button>
          <Button variant="danger" onClick={handleDelete}>Confirm</Button>
        </Modal.Footer>
      </Modal>

            <Table striped bordered hover size="sm">
                <thead>
                    <tr >
                        <th>ID</th>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Amount $</th>
                        <th>Time</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data?.map((value, key) => {
                            return (
                                <tr key={key}>
                                    <td>{value.id}</td>
                                    <td>{value.name}</td>
                                    <td>{value.description}</td>
                                    <td>{value.amount}</td>
                                    <td>{value.time}</td>
                                    <td>

                                    <Button variant="info">EDIT</Button>{' '}
                                    <Button variant="danger" onClick={() => handleOpenDeleteModal(value.id)}>DELETE</Button>{' '}
                                    </td>
                                </tr>
                            )
                        })
                    }
                </tbody>
                {/* <div>
                <Pagination size="sm">{items}</Pagination>
                </div> */}
            </Table>
        </>
    );
}

export default TableData;
