import React, { useState, useEffect } from "react";
import axios from "axios";

const MoneyBalance = () => {
  const [expenses, setExpenses] = useState([]);
  const [incomes, setIncomes] = useState([]);
  const [balance, setBalance] = useState(0);

  useEffect(() => {
    async function fetchData() {
      const expenseValues = await axios.get("http://localhost:3000//expenses");
      const incomeValues = await axios.get("http://localhost:3000//incomes");

      setExpenses(expenseValues.data);
      setIncomes(incomeValues.data);
    }

    fetchData();
  }, []);

  useEffect(() => {
    const expenseAmounts = expenses.map((expenses) => expense.amount);
    const incomeAmounts = incomes.map((incomes) => income.amount);

    const totalExpenseAmount = expenseAmounts.reduce(
      (total, amount) => total + amount,
      0
    );
    const totalIncomeAmount = incomeAmounts.reduce(
      (total, amount) => total + amount,
      0
    );

    setBalance(totalIncomeAmount - totalExpenseAmount);
  }, [expenses, incomes]);

  return <h1>Current Balance {balance}</h1>;
};

export default MoneyBalance;
