import React, { useState, useEffect } from "react";
import axios from "axios";

const MoneyBalance = () => {
  const [data, setData] = useState([]);
  const [balance, setBalance] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      const incomeValues = await axios
        .get("/http://localhost:3000/incomes")
        .then((response) => setData(response.data));

      const expenseValues = await axios
        .get("/http://localhost:3000/expenses/expenses")
        .then((response) => setData(response.data));

      const incomeTotal = incomeValues.data.reduce(
        (total, item) => total + item.amount,
        0
      );
      const expenseTotal = expenseValues.data.reduce(
        (total, item) => total + item.amount,
        0
      );
      setBalance(incomeTotal - expenseTotal);
    };
  }, []);

  return <h1>Current Balance {balance}</h1>;
};

export default MoneyBalance;
