import React, { useState, useEffect } from "react";
import axios from "axios";

const MoneyBalance = () => {
  const [expenses, setExpenses] = useState([]);
  const [incomes, setIncomes] = useState([]);
  const [dataExpense, setDataExpense] = useState([]);
  const [DataIncome, setDataIncome] = useState([]);
  const [balance, setBalance] = useState(0);

  useEffect(() => {
    async function fetchData() {
      const expenseValues = await axios
        .get("http://localhost:3000//expenses")
        .then((response) => setDataExpense(response.data));

      const incomeValues = await axios
        .get("http://localhost:3000//incomes")
        .then((response) => setDataIncome(response.data));
    }

    fetchData();
  }, []);

  useEffect(() => {
    const expenseAmounts = data.map((amount) => expenses.amount);
    const incomeAmounts = data.map((amount) => incomes.amount);

    const totalExpenseAmount = expenseAmounts.reduce(
      (total, amount) => total + amount,
      0
    );
    const totalIncomeAmount = incomeAmounts.reduce(
      (total, amount) => total + amount,
      0
    );

    setBalance(totalIncomeAmount - totalExpenseAmount);
  }, [expenses, incomes]);

  return (
    <h1
      style={{
        marginTop: "1rem",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      Current Balance {balance} {"€"}
    </h1>
  );
};

export default MoneyBalance;
