import React, { useState, useEffect } from "react";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Modal from "react-bootstrap/Modal";

const TableData = (props) => {
  const [data, setData] = useState([]);
  const [show, setShow] = useState(false);
  const [deleteId, setDeleteId] = useState("");
  const [validated, setValidated] = useState(false);
  const [select, setSelectEdit] = useState("");
  const [name, setNameEdit] = useState("");
  const [amount, setAmountEdit] = useState("");
  const [description, setDescriptionEdit] = useState("");

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const navigate = useNavigate();
  useEffect(() => {
    axios
      .get(`http://localhost:3000/${props.url}`)
      .then((response) => setData(response.data));
  }, []);

  const handleOpenDeleteModal = (ID) => {
    setDeleteId(ID);
    setShow(true);
  };

  const handleDelete = () => {
    axios
      .delete(`http://localhost:3000/${props.url}/${deleteId}`)
      .then((response) => {
        axios
          .get(`http://localhost:3000/${props.url}`)
          .then((response) => setData(response.data))
          .finally(handleClose());
      });
  };

  const onSelectEdit = (event) => {
    setSelectEdit(event.target.value);
  };

  const onNameChangeEdit = (event) => {
    setNameEdit(event.target.value);
  };

  const onDescriptionChangeEdit = (event) => {
    setDescriptionEdit(event.target.value);
  };

  const onAmountChangeEdit = (event) => {
    setAmountEdit(event.target.value);
  };

  const urlExtension = select === "expense" ? "expenses" : "incomes";

  const onSubmit = async (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    setValidated(true);

    return (
      <>
        <Modal
          show={show}
          onHide={handleClose}
          backdrop="static"
          keyboard={false}
        >
          <Modal.Header closeButton>
            <Modal.Title>Warning</Modal.Title>
          </Modal.Header>
          <Modal.Body>Are you sure you want to delete this item?</Modal.Body>
          <Modal.Footer>
            <Button variant="success" onClick={handleClose}>
              Close
            </Button>
            <Button variant="danger" onClick={handleDelete}>
              Confirm
            </Button>
          </Modal.Footer>
        </Modal>

        <Modal show={show} onHide={handleClose}>
          <Form noValidate validated={validated} onSubmit={onSubmit}>
            <Modal.Header closeButton></Modal.Header>
            <Modal.Body>
              <Form.Group
                className="mb-3"
                controlId="exampleForm.ControlInput1"
              >
                <Form.Label>Name</Form.Label>
                <Form.Control
                  onChange={onNameChangeEdit}
                  required
                  type="text"
                  autoFocus
                />
                <Form.Control.Feedback type="invalid">
                  Name not specified
                </Form.Control.Feedback>

                <Form.Label>Amount</Form.Label>
                <Form.Control
                  onChange={onAmountChangeEdit}
                  required
                  type="number"
                  autoFocus
                />
                <Form.Control.Feedback type="invalid">
                  Amount not specified
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group
                className="mb-3"
                controlId="exampleForm.ControlTextarea1"
              >
                <Form.Label>Desciption</Form.Label>
                <Form.Control
                  onChange={onDescriptionChangeEdit}
                  required
                  type="text"
                  as="textarea"
                  rows={2}
                />
                <Form.Control.Feedback type="invalid">
                  Description not specified
                </Form.Control.Feedback>
              </Form.Group>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="danger" onClick={handleClose}>
                Cancel
              </Button>
              <Button variant="success" type="submit">
                Save
              </Button>
            </Modal.Footer>
          </Form>
        </Modal>

        <Table striped bordered hover size="sm">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Description</th>
              <th>Amount $</th>
              <th>Time</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {data?.map((value, key) => {
              return (
                <tr key={key}>
                  <td>{value.id}</td>
                  <td>{value.name}</td>
                  <td>{value.description}</td>
                  <td>{value.amount}</td>
                  <td>{value.time}</td>
                  <td>
                    <Button variant="info">EDIT</Button>{" "}
                    <Button
                      variant="danger"
                      onClick={() => handleOpenDeleteModal(value.id)}
                    >
                      DELETE
                    </Button>{" "}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </>
    );
  };
};

export default TableData;
