import React from "react";
import Table from "react-bootstrap/Table";

const DashboardTables = () => {
  return (
    <Table striped bordered hover size="sm">
      <thead>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>Description</th>
          <th>Amount $</th>
          <th>Time</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {data?.map((value, key) => {
          return (
            <tr key={key}>
              <td>{value.id}</td>
              <td>{value.name}</td>
              <td>{value.description}</td>
              <td>{value.amount}</td>
              <td>{value.time}</td>
              <td>
                <Button
                  variant="warning"
                  onClick={() => handleShowEdit(value.id)}
                >
                  EDIT
                </Button>{" "}
                <Button
                  variant="danger"
                  onClick={() => handleOpenDeleteModal(value.id)}
                >
                  DELETE
                </Button>{" "}
              </td>
            </tr>
          );
        })}
      </tbody>
    </Table>
  );
};
