import React, { useState, useEffect } from "react";
import axios from "axios";

const MoneyBalance = () => {
  const [data, setData] = useState([]);
  const [balance, setBalance] = useState("");
  const [incomeData, setIncomeData] = useState("");
  const [expenseData, setExpenseData] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      const incomeValues = await axios.get("/http://localhost:3000/incomes");
      const expenseValues = await axios.get(
        "/http://localhost:3000/expenses/expenses"
      );

      const incomeTotal = incomeValues.data.reduce(
        (total, item) => total + item.amount,
        0
      );
      const expenseTotal = expenseValues.data.reduce(
        (total, item) => total + item.amount,
        0
      );
      setBalance(incomeTotal - expenseTotal);
    };

    fetchData();
  }, [incomeData, expenseData]);

  return (
    <h1
      style={{
        marginTop: "1rem",
        marginBottom: "1rem",
        color: "#1a1a1a",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        width: "100%",
      }}
    >
      Current Balance {`balance ${"€"}`}
    </h1>
  );
};

export default MoneyBalance;
