import React, { useState, useEffect } from "react";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Pagination from "react-bootstrap/Pagination";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Modal from "react-bootstrap/Modal";

// let active = 1;
// let items = [];
// for (let number = 1; number <= 5; number++) {
//   items.push(
//     <Pagination.Item key={number} active={number === active}>
//       {number}
//     </Pagination.Item>,
//   );
// }

const TableData = (props) => {
  const [data, setData] = useState([]);
  const [show, setShow] = useState(false);
  const [deleteId, setDeleteId] = useState("");

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const navigate = useNavigate();
  useEffect(() => {
    axios
      .get(`http://localhost:3000/${props.url}`)
      .then((response) => setData(response.data));
  }, []);

  const handleOpenDeleteModal = (ID) => {
    setDeleteId(ID);
    setShow(true);
  };

  const handleDelete = () => {
    axios
      .delete(`http://localhost:3000/${props.url}/${deleteId}`)
      .then((response) => {
        axios
          .get(`http://localhost:3000/${props.url}`)
          .then((response) => setData(response.data))
          .finally(handleClose());
      });
  };

  return (
    <>
      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Warning</Modal.Title>
        </Modal.Header>
        <Modal.Body>Are you sure you want to delete this item?</Modal.Body>
        <Modal.Footer>
          <Button variant="success" onClick={handleClose}>
            Close
          </Button>
          <Button variant="danger" onClick={handleDelete}>
            Confirm
          </Button>
        </Modal.Footer>
      </Modal>

      <Modal show={show} onHide={handleClose}>
        <Form noValidate validated={validated} onSubmit={onSubmit}>
          <Modal.Header closeButton>
            <Form.Select
              aria-label="Default select example"
              required
              onChange={onSelectChange}
            >
              <option value="">Select Group</option>
              <option value="expense">Expense Group</option>
              <option value="income">Income Group</option>
            </Form.Select>
          </Modal.Header>

                    <Modal.Body>
                      <Form.Label>Name</Form.Label>
                      <Form.Control
                        onChange={onNameChange}
                        required
                        type="text"
                        placeholder="Please full the empty field"
                        autoFocus
                      />

            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Name</Form.Label>
              <Form.Control
                onChange={onNameChange}
                required
                type="text"
                placeholder="Please full the empty field"
                autoFocus
              />
              <Form.Control.Feedback type="invalid">
                Name not specified
              </Form.Control.Feedback>

              <Form.Label>Amount</Form.Label>
              <Form.Control
                onChange={onAmountChange}
                required
                type="number"
                placeholder="Please enter amount $..."
                autoFocus
              />
              <Form.Control.Feedback type="invalid">
                Amount not specified
              </Form.Control.Feedback>
            </Form.Group>
            <Form.Group
              className="mb-3"
              controlId="exampleForm.ControlTextarea1"
            >
              <Form.Label>Desciption</Form.Label>
              <Form.Control
                onChange={onDescriptionChange}
                required
                type="text"
                placeholder="Please enter a description..."
                as="textarea"
                rows={2}
              />
              <Form.Control.Feedback type="invalid">
                Description not specified
              </Form.Control.Feedback>
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="danger" onClick={handleClose}>
              Cancel
            </Button>
            <Button variant="success" type="submit">
              Save
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>

      <Table striped bordered hover size="sm">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Description</th>
            <th>Amount $</th>
            <th>Time</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data?.map((value, key) => {
            return (
              <tr key={key}>
                <td>{value.id}</td>
                <td>{value.name}</td>
                <td>{value.description}</td>
                <td>{value.amount}</td>
                <td>{value.time}</td>
                <td>
                  <Button variant="info">EDIT</Button>{" "}
                  <Button
                    variant="danger"
                    onClick={() => handleOpenDeleteModal(value.id)}
                  >
                    DELETE
                  </Button>{" "}
                </td>
              </tr>
            );
          })}
        </tbody>
        {/* <div>
                <Pagination size="sm">{items}</Pagination>
                </div> */}
      </Table>
    </>
  );
};

export default TableData;
